import '../pages/agregar_establecimiento-page/agregar_establecimiento.css';
import "bootstrap/dist/css/bootstrap.min.css";
import $ from 'jquery';
//import {  Redirect } from 'react-router-dom';

export function Form_Add_establecimiento() {

  let hasDecimal = false;


  async function submitHandler(e){
    e.preventDefault();

    const name = $('#name').val();
    const address = $('#address').val();
    const parkinglot = $('#parkinglot').val();
    const hourPrice = $('#hourPrice').val();

    if(name === '' || address ==='' || parkinglot === '' || hourPrice === ''){
      alert('Favor de rellenar los campos faltantes');
    }else if(parkinglot < 100){
      alert("tiene que haber mas de 100 espacios de estacionamiento")
    }else{

      const body = {
        isActive: true,
        name: name,
        address: address,
        parkinglot: parkinglot,
        hourPrice: hourPrice
      }

      const response = await fetch(`http://localhost:3001/api/establecimientos`,{
        method: 'POST',
        headers: { 'Content-Type': 'applic-ation/json'},
        body: JSON.stringify(body)
      });
      const respJson = await response.json();
      console.log(respJson);
    }

  }

  const onlyNumber = (event) => {
   if (!/[0-9]/.test(event.key)) {
      event.preventDefault();
    }
  }

  const onlyNumberFloat = (event) => {
    const hourPrice = $('#hourPrice').val();
    if(/\.+/.test(hourPrice)){
      hasDecimal = true;
    }else{
      hasDecimal = false;
    }
    if(/[0-9]\.[0-9][0-9]$/.test(hourPrice)){
      event.preventDefault();
    }
    if(/(\.)/.test(event.key) && !hasDecimal){
      return;
    }else if (!/[0-9]/.test(event.key)) {
      event.preventDefault();
    }
  }

  /*
    <label>Subir logo de establecimiento</label><br></br>
    <input type="file"></input><br></br>
    <label >Selecciona una hora de apertura y cierre:</label><br></br>
    <input type="time" name="appt"></input>
    <input type="time" name="appt"></input><br></br>
  */

    return <>

        <form onSubmit={submitHandler}>
            <label>Nombre del establecimiento </label><br></br>
            <input type="text" id="name"placeholder="Establecimiento los cuates"></input><br></br>
            <label>Direccion </label><br></br>
            <input type="text" id="address"placeholder=" Agustin de iturbide #1500 A "></input><br></br>
            <label>Numero de cajones </label><br></br>
            <input type="text" id="parkinglot" placeholder="200" onKeyPress={onlyNumber}></input><br></br>
            <label>Precio por hora</label><br></br>
            <input type="text" id="hourPrice" placeholder="200" onKeyPress={onlyNumberFloat}></input><br></br>
            <div class="btnCenter">
                <input type="submit" value="Crear establecimiento"></input>
            </div>

        </form>

    </>
}
