import '../pages/agregar_establecimiento-page/agregar_establecimiento.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useHistory } from 'react-router-dom';
import $ from 'jquery';
import { useEffect, useState } from "react";

export function Form_edit_establecimiento() {

  let hasDecimal = false;
  const history = useHistory();

  const [gotInfo, setGotInfo] = useState(false);
  const establecimientoID = location.pathname.split('/')[2].split(',')[0];
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  //const [parkingLot, setName] = useState("");
  const [hourPrice, setHourPrice] = useState(0);
  const [parkinglots, setParkinglots] = useState(0);
  const [id, setId] = useState(0);


  async function submitHandler(e) {
    e.preventDefault();

    const name = $('#name').val();
    const address = $('#address').val();
    const parkinglot = $('#parkinglot').val();
    const hourPrice = $('#hourPrice').val();

    if (name === '' | address === '' | parkinglot === '' | hourPrice === '') {
      alert('Favor de rellenar los campos faltantes');
    } else if (parkinglot < 100) {
      alert('tiene que haber mas de 100 espacios de estacionamiento');
    } else {
      const body = {
        isActive: true,
        name: name,
        address: address,
        parkinglot: parkinglot,
        hourPrice: hourPrice,
      };
      console.log(body);
      const response = await fetch(
        `http://localhost:3001/api/establecimientos/${id}`,
        {
          method: 'PATCH',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(body),
        }
      );
      const respJson = await response.json();
      console.log(respJson);
      history.push("/");
    }
  }

  const onlyNumber = (event) => {
    if (!/[0-9]/.test(event.key)) {
      event.preventDefault();
    }
  };

  const onlyNumberFloat = (event) => {
    const hourPrice = $('#hourPrice').val();
    if (/\.+/.test(hourPrice)) {
      hasDecimal = true;
    } else {
      hasDecimal = false;
    }
    if (/[0-9]\.[0-9][0-9]$/.test(hourPrice)) {
      event.preventDefault();
    }
    if (/(\.)/.test(event.key) && !hasDecimal) {
      return;
    } else if (!/[0-9]/.test(event.key)) {
      event.preventDefault();
    }
  };

  useEffect(() => {

    async function getEstablecimiento() {
      if (!gotInfo) {
        const response = await fetch(`http://localhost:3001/api/establecimientos/findById/${establecimientoID}`);

        const respJson = await response.json();
        if(respJson.success){
          setName(respJson.data.name)
          setAddress(respJson.data.address)
          setHourPrice(respJson.data.hourPrice)
          setParkinglots(respJson.data.parkinglots);
          setId(respJson.data._id);
          setGotInfo(true);

        }
      }
    }
    getEstablecimiento();

  }, [])




  return (
    <>
      <form onSubmit={submitHandler}>
        <label>Nombre del establecimiento </label>
        <br></br>
        <input
          type="text"
          id="name"
          placeholder={name}
        ></input>
        <br></br>
        <label>Direccion </label>
        <br></br>
        <input
          type="text"
          id="address"
          placeholder={address}
        ></input>
        <br></br>
        <label>Numero de cajones </label>
        <br></br>
        <input
          type="text"
          id="parkinglot"
          placeholder={parkinglots}
          onKeyPress={onlyNumber}
        ></input>
        <br></br>
        <label>Precio por hora</label>
        <br></br>
        <input
          type="text"
          id="hourPrice"
          placeholder={hourPrice}
          onKeyPress={onlyNumberFloat}
        ></input>
        <br></br>
        <div className="btnCenter">
          <input type="submit" value="Crear establecimiento"></input>
        </div>
      </form>
    </>
  );
}
