import styled from 'styled-components';
import { useHistory   } from 'react-router-dom';
import $ from 'jquery';
export function Form_reservar(props) {

  /*const  submitHandler = async(e) =>{

  }*/

  const history = useHistory();
  async function submitHandler(e) {
    e.preventDefault();
    const hourPrice = $('#hora').val();
    console.log(hourPrice)
  }


  console.log(props)

  return (
    <>
      <Reservacion>
      <form onSubmit={submitHandler}>
        <p className="p-aviso">Haz tu reservación fácil y rápido.</p>
        <div className="box-flex">
          <p>Hora de inicio</p>
          <input id="hora" type="time"></input>
        </div>
        <div className="box-flex">
          <p>Tarifa</p>
          <p>{props.hourPrice}$</p>
        </div>
        <input type="submit" value="Crear Reservacion"></input>
          </form>
      </Reservacion>
    </>
  );
}

const Reservacion = styled.div`
  .box-flex {
    display: flex;
    padding-top: 5px;
    padding-bottom: 5px;
  }
  .p-aviso {
    margin: 0px;
    padding: 5px;
    font-weight: bold;
  }

  p {
    padding: 5px;
    margin-left: 0px;
  }
`;
const Boton = styled.div`
  float: right;
  width: fit-content;
  background-color: rgb(201, 36, 36);
  padding-top: 5px;
  padding-bottom: 5px;
  padding-left: 2vw;
  padding-right: 2vw;
  margin-top: 15px;
  border-radius: 5px;
  border-color: transparent;
  color: rgb(236, 242, 247);
  font-family: Herculanum, 'Eras Light ITC', sans-serif;

  &:hover {
    background-color: rgb(168, 30, 30);
  }
`;
